import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import github from '../../../../assets/images/github.svg'
import google from '../../../../assets/images/google.svg'
import { storeApiData } from '../../../../api/api'
import axios from 'axios'

const LoginItem = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [tokenData, setTokenData] = useState('')
    const [userData, setUserData] = useState('')

    const checkRole = () => {
        let rolee = userData.role

        if (rolee === 'admin') {
            window.location = `/admin/${userData.slug}`
        }else if (rolee === 'company') {
            window.location = `/company/${userData.slug}`
        }else if (rolee === 'job seeker') {
            window.location = `/user/${userData.slug}`
        }
    }

    const loginHandler = () => {
        function mockAPICall(id) {
            return new Promise((resolve, reject) => {
                setTimeout(() => {
                    resolve(`Response from API with ID ${id}`);
                }, 1000); // Simulate delay of 1 second
            });
        }

        // Chain async API calls
        function chainAPICalls() {
            mockAPICall(1)
              .then(response1 => {
                // console.log(response1);
                // return mockAPICall(2); // Return a Promise to chain the next call
              })
              .then(response2 => {
                return localStorage.clear()
                // return getToken()
                // console.log(response2);
                // return mockAPICall(3); // Return a Promise to chain the next call
              })
              .then(response3 => {
                // return checkProfile()
                // console.log(response3);
              })
              .catch(error => {
                console.error('Error:', error);
              });
          }
  
            // Call the function to initiate the chain
            // chainAPICalls();
        
        const chainLoginCall = () => {
            mockAPICall(1)
            .then(respone1 => {
                return getToken()
            })
            .then(response2 => {
                return checkProfile()
            })
            .then(response3 => {
                return checkRole()
                // return checkProfile()
            })
            .then(response4 => {
                // return checkRole()
            })
            .catch(error => {
                console.error('Error:', error);
            });
        }

        // localStorage.clear()

        // chainAPICalls();

        chainLoginCall()


        const getToken = async () => {
            await storeApiData(`auth/login`, { email, password })
            .then((response)=>{localStorage.setItem('token', response.data.token); setTokenData(response.data.token)})
            .catch((response)=>console.log(response.data))
        }

        const checkProfile = async () => {
            await axios.get('http://localhost:8000/auth/user-profile', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`, 
                //   Authorization: `Bearer ${tokenData.token}`, 
                },
            }).then((response)=>setUserData(response.data.data))
            // .then(checkRole())
            .catch((response)=>console.log(response.data))
        }

        // const combine = async () => {
        //     await getToken()
        //     .then(checkProfile())
        //     .catch(console.log('error cannot login'))
        // }

        // combine()

        // proccess()
        // localStorage.clear()
    }

    const login = () => {
        // localStorage.removeItem('email')
        // localStorage.setItem('email', email)
        // console.log(localStorage.getItem('email'));
        const getToken = async () => {
            const response = await storeApiData(`auth/login`, {email, password});
            if (response.status===true) {
                setTokenData(response.data);
            }else {
                console.log(response);
            }
        }
        getToken();
        // localStorage.clear()

        localStorage.setItem('token', tokenData.token)

        const getUser = async () => {
            try {
                const response = await axios.get('http://localhost:8000/auth/user-profile', {
                  headers: {
                    //Authorization: `Bearer ${localStorage.getItem('token')}`, 
                    Authorization: `Bearer ${tokenData.token}`, 
                  },
                });
                setUserData(response.data.data);
              } catch (error) {
                console.error('Error fetching data:', error);
              }
        }

        getUser();

        let rolee = userData.role

        if (rolee === 'admin') {
            window.location = `/admin/${userData.slug}`
        }else if (rolee === 'company') {
            window.location = `/company/${userData.slug}`
        }else if (rolee === 'job seeker') {
            window.location = `/user/${userData.slug}`
        }
    }

    console.log(userData)
    return (
        <section className='login'>
            <div className="container">
                <div className="auth-div">
                    <form>
                        <div className="form">
                            <div>
                                <input type="email" className='form-control' name="email" placeholder='user email' value={email} onChange={(e) => setEmail(e.target.value)}/>
                            </div>
                            <div>
                                <input type="password" className='form-control' name="password" placeholder='user password' value={password} onChange={(e) => setPassword(e.target.value)}/>
                            </div>
                            <button type='button' className="button" onClick={loginHandler}>
                                <div>
                                    {/* <img src='' alt='' height='15px' width='15px'/> */}
                                    <span>Login</span>
                                </div>
                            </button>
                            <div className="forgot">
                                <Link to='/sign-up'>Don't have any account?</Link>
                                <Link to='/forgot-password'>Forgot your password?</Link>
                            </div>
                            <div className="social">
                                <img src={github} alt="github logo" />
                                <img src={google} alt="google logo" />
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </section>
    )
}

export default LoginItem
